enrich_function:
function (matches, axios) { 
  var data = matches.map(function (m) { return { query: m }});
  return axios.post("http://ip-api.com/batch", data) 
}

for_each_result:
function (value, input, enrich) {
  var out = input + ": ";
  if (value.status === "success") {
    out += [value.regionName, value.city, value.country].join(", ")
  } else {
     out += value.message
  }
  return out;
}

function (value, input, enrich) {
  return input + " -> " + value.dns.ip + ", " + value.dns.geo;
}

